import React from 'react';
import './index.js'

let date=new Date();
let heading={ };
let hour=date.getHours();
let text=" ";
if (hour>=1 ) {
  text="Good Morning";
  heading.color="green";
}
else if (hour>=12 && hour<19) {
  text=" Good Afternoon";
   heading.color="orange";
}
else{
  text="Good Night";
   heading.color="black";
}

function Project2(){

    return( <h2 >Hello <span style={heading}> {text}</span> Sir! </h2>);

}

export default Project2;
