const lname="sarwar"
let date= new Date();
var myVar = setInterval(myTimer, 500);

function myTimer() {
  var d = new Date();
  var t = d.toLocaleTimeString();
  document.getElementById("demo").innerHTML ="Now Time Is"+" "+t;
}

ReactDOM.render(
  <>
 <h1> This is list of five netflix series </h1>
 <p>there are following five netflix series</p>
 <ol>  
 <li> thisn first</li>
 <li> thisn second</li>
 <li> thisn three</li>
 <li> thisn four</li>
 <li> thisn five</li>
  </ol>
  <h3>{`my first name is ${fname} and my last name is ${lname}`}</h3>
   <h3>{`today date is ${date.toLocaleDateString()} and time is ${date.toLocaleTimeString()} `}</h3>
   <h1 id="demo"> </h1>
  </>
  ,
  document.getElementById('root')
);
 //end root
